package com.my.common.sequence;import com.my.common.cache.RedisCache;import org.apache.commons.lang.StringUtils;import java.text.DecimalFormat;/** * Created by huqilong on 2017/7/24. */public class SequenceUtil {    public static String nextSequence(String prefix, String key, String format){        Long sequence = null;        try {            if(!RedisCache.exists(key)){                RedisCache.set(key,1);                sequence = 1l;            }else {                sequence = RedisCache.incr(key);            }        } catch (Exception e) {            e.printStackTrace();        }        prefix = StringUtils.trimToEmpty(prefix);        return prefix+format(sequence,format);    }    public static String format(Long sequence,String format){        DecimalFormat df = new DecimalFormat(format);        return df.format(sequence);    }}